function img=drawEllipse(img,a,b,cen)%image with ellipse = function ( image without ellipse, a value, b value,%centre of ellipse%where a , b are values in equation%(x^2/a^2)+(y^2/b^2)=1, equation for ellipsefor i=1:size(img,1)    for j=1:size(img,2)        x=i-cen(1);        y=j-cen(2);                k1=(x/a)^2;        k2=(y/b)^2;        k=k1+k2;%         if (a<30 || b<30)%             approx=1.2;%         else%             approx=1.1;%         end                if (k<=1)            img(i,j)=1;        end    endendend